import { useState, useEffect, useRef } from "react";
import axios from 'axios';
import Card from "./Card";

const Deck = () =>{
  // initial value of null will be replace on render due to useEffect
  const [deck, setDeck] = useState(null);
  const [currentCardImg, setCurrentCardImg] = useState('https://deckofcardsapi.com/static/img/back.png')
   // set deck to a new deck 
   useEffect(()=>{
    axios
    .get('https://deckofcardsapi.com/api/deck/new/draw/?count=1')
    .then(res=>{
      setDeck(`https://deckofcardsapi.com/api/deck/${ res.data.deck_id}/draw/?count=1`)
    })
  }, [])
  // keep track of cards clicked to trigger draw with useEffect
  let card = 0
  let cards = useRef(card)
  const drawCard = () => {
    cards.current = card++
  };
  console.log(cards)
  // // draw from deck until cards are gone, then show back of deck
  useEffect(()=>{
    axios
    .get(deck)
    .then(res=>{
      setCurrentCardImg(res.data.cards[0].image);
    })
  },[cards])
  console.log(deck)

  return(
    <>
      <img src={currentCardImg}/>
      <br></br>
      <button onClick={drawCard} ref={cards}> Draw</button>
    </>
  )
}

export default Deck;
