import { useState, useEffect, useRef } from "react";
import axios from 'axios';
import Card from "./Card";

const Deck = () =>{
  // initial value of null will be replaced on render due to useEffect
  const [deck, setDeck] = useState('https://deckofcardsapi.com/static/img/back.png');
   // set deck to a new deck 
   useEffect(()=>{
    axios
    .get('https://deckofcardsapi.com/api/deck/new/draw/?count=1')
    .then(res=>{
      setDeck(`https://deckofcardsapi.com/api/deck/${ res.data.deck_id}/draw/?count=1`)
    })
  }, [])
  console.log(deck)
  return(
    <>
      <img src={deck}/>
      <br></br>
      <button> Draw</button>
    </>
  )
}

export default Deck;
