import { useState, useEffect } from "react";
import axios from 'axios';
import Card from "./Card";

const Deck = () =>{
  // initial value of null will be replace on render due to useEffect
  const [deck, setDeck] = useState();
  const [currentCardImg, setCurrentCardImg] = useState('https://deckofcardsapi.com/static/img/back.png')
  // set deck to a new deck 
  useEffect(()=>{
    axios
    .get('https://deckofcardsapi.com/api/deck/new/')
    .then(res=>{
      setDeck(`https://deckofcardsapi.com/api/deck/${ res.data.deck_id}/draw/?count=1`)
    })

    return
  }, [])
  // draw from deck until cards are gone, then show back of deck
  const draw = (deck) =>{
    useEffect(()=>{
      axios
        .get(deck)
        .then(data=>{
          console.log(data.data)
        })
    })
  }

  return(
    <div>
      <Card src={currentCardImg}/>
      <button >Draw</button>
    </div>
  )
}

export default Deck;
