import { useState, useEffect } from "react";
import axios from 'axios';

const Deck = () => {
  const [deckId, setDeckId] = useState(null);
  const [currentCardImg, setCurrentCardImg] = useState('https://deckofcardsapi.com/static/img/back.png');

  // Define the draw function outside of useEffect
  const draw = async () => {
    if (deckId) {
      const response = await axios.get(`https://deckofcardsapi.com/api/deck/${deckId}/draw/?count=1`);
      setCurrentCardImg(response.data.cards[0].image);
    }
  };

  useEffect(() => {
    axios.get('https://deckofcardsapi.com/api/deck/new/draw/?count=1')
      .then(res => {
        setDeckId(res.data.deck_id);
      });
  }, []);

  useEffect(() => {
    // Use the draw function here
    draw();
  }, [deckId]);

  const handleClick = () => {
    // Do something when the button is clicked
    // For example, trigger another draw or perform an action
    draw();
  };

  return (
    <>
      <img src={currentCardImg} alt="current card"/>
      <br></br>
      <button onClick={handleClick}>Draw</button>
    </>
  );
}

export default Deck;
